mapper.select:
SELECT USER_NM
FROM C##ECODEV.COMM_USER_MST
WHERE USER_ID = #{userId}
  AND USER_PASSWORD = #{password}

SELECT USER_PASSWORD
FROM C##ECODEV.COMM_USER_MST
WHERE USER_ID = #{userId}

SELECT COUNT(*)
FROM C##ECODEV.COMM_USER_MST
WHERE USER_ID = #{userId}

mapper.insert:
INSERT INTO C##ECODEV.COMM_USER_MST
     (
       USER_ID
     , USER_PASSWORD
     , USER_NM

     , USER_ADDR


     , USER_HP_NO

     , CRER_ID
     )
VALUES
     (
       #{userId}
     , #{password}
     , #{userNm}

     , #{userAddr}


     , #{userHpNo}

     , 'admin'
     )
