mapper.insert:
INSERT INTO angmoa.LOGIN_LOG
     (
       LOGIN_LOG_ID
     , USER_ID
     , IP
     , LOGIN_SUCCESS
     , LOGIN_DATE
     )
VALUES
     (
       (SELECT LOGIN_LOG_ID FROM (SELECT IFNULL(MAX(LOGIN_LOG_ID)+1,1) AS LOGIN_LOG_ID FROM angmoa.LOGIN_LOG) LOGIN_LOG)
     , #{userId}
     , #{ip}
     , #{loginYn}
     , NOW()
     )

mapper.update:
UPDATE angmoa.COMM_USER_MST
   SET LOGIN_FAIL = 0
 WHERE USER_ID = #{userId}

mapper.select:
