  FROM angmoa.COMM_USER_MST
 WHERE USER_ID = #{userId}

SELECT COUNT(*)
  FROM angmoa.COMM_USER_MST
 WHERE USER_ID = #{userId}

   AND USER_NM = #{userNm}

